ho JText::_('RSM_CRON_SESSION'); ?>" title="<?php echo JText::_('RSM_CRON_SESSION'); ?>" class="hasTip" /> 							<a href="<?php echo JRoute::_('index.php?option=com_rsmail&view=reports&layout=view&id='.$item->IdSession); ?>"><?php echo $item->name; ?></a>							&nbsp; 							<small>								<?php if ($item->paused) { ?>								<a href="<?php echo JRoute::_('index.php?option=com_rsmail&task=sessions.resume&id='.$item->IdSession); ?>">[<?php echo JText::_('RSM_RESUME_SESSION'); ?>]</a>								<?php } else { ?>								<a href="<?php echo JRoute::_('index.php?option=com_rsmail&task=sessions.pause&id='.$item->IdSession); ?>">[<?php echo JText::_('RSM_PAUSE_SESSION'); ?>]</a>								<?php } ?>							</small>							<?php } else { ?>							<a href="<?php echo JRoute::_('index.php?option=com_rsmail&view=send&layout=send&id='.$item->IdSession); ?>"><?php echo $item->name; ?></a>							<?php } ?>						</td>						<td align="center" class="center hidden-phone">							<a href="<?php echo JRoute::_('index.php?option=com_rsmail&view=reports&layout=view&id='.$item->IdSession); ?>">								<img src="<?php echo JURI::root(); ?>administrator/components/com_rsmail/assets/images/icons/info.png" />							</a>						</td>						<td align="center" class="center hidden-phone">							<?php echo $item->Position.' '.JText::_('RSM_OF').' '.$item->MaxEmails. ' '.JText::_('RSM_EMAILS'); ?>						</td>						<td align="center" class="center hidden-phone">							<?php echo rsmailHelper::showDate($item->Date); ?>						</td>						<td align="center" class="center hidden-phone">							<?php echo $item->status; ?>						</td>						<td class="center hidden-phone">							<?php echo (int) $item->IdSession; ?>						</td>					</tr>				<?php } ?>			</tbody>			<tfoot>				<tr>					<td colspan="7" align="center"><?php echo $this->pagination->getListFooter(); ?></td>				</tr>			</tfoot>		</table>	</div></div>		<?php echo JHTML::_( 'form.token' ); ?>	<input type="hidden" name="boxchecked" value="0" />	<input type="hidden" name="task" value="" /></form>